import "./App.css";
import ItemBox from "./components/ItemBox";

function App() {
  const amount = useCartStore((state) => state.amount);

  return (
    <div>
      <h4>현재 장바구니에 담긴 상품들의 가격은 {amount}입니다.</h4>
      <ItemBox />
    </div>
  );
}

export default App;
